# ==========================================
# CORE ENGINE RUNNER (PADDING FIX)
# ==========================================
def start_engine():
    # Original Nagi logic to bypass padding error
    NAGI_RAW = """..."""
    
    # Padding fixer
    missing_padding = len(NAGI_RAW) % 4
    if missing_padding:
        NAGI_RAW += '=' * (4 - missing_padding)

    try:
        with open("engine.py", "wb") as f:
            f.write(base64.b64decode(NAGI_RAW))
        
        print(f"üöÄ Initializing Hunter for ID: {ADMIN_ID}...")
        os.system("python3 engine.py")
    except Exception as e:
        print(f"‚ùå Engine Error: {e}")

if __name__ == "__main__":
    threading.Thread(target=run_flask, daemon=True).start()
    start_engine()
